    if any(word in q for word in ['fertilizer', 'рдЦрд╛рдж', 'рдЦрдд', 'р▓Чр│Кр▓мр│Нр▓мр▓░', 'роЙро░роорпН', 'р░Ор░░р▒Бр░╡р▒Б', 'р┤╡р┤│р┤В', 'ркЦрк╛ркдрк░', 'риЦри╛риж']):
        return FARMING_KNOWLEDGE['fertilizer'].get(language_code, FARMING_KNOWLEDGE['fertilizer']['en'])
    
    elif any(word in q for word in ['pest', 'insect', 'рдХреАрдЯ', 'рдХреАрдЯрдХ', 'р▓Хр│Ар▓Я', 'рокрпВроЪрпНроЪро┐', 'р░кр▒Бр░░р▒Бр░Чр▒Б', 'р┤Хр╡Ар┤Яр┤В', 'ркЬркВркдрлБ', 'риХрйАрйЬрйЗ', 'рдХрд┐рдбреЛ']):
        return FARMING_KNOWLEDGE['pest_control'].get(language_code, FARMING_KNOWLEDGE['pest_control']['en'])
    
    elif any(word in q for word in ['water', 'irrigation', 'рдкрд╛рдиреА', 'рд╕рд┐рдВрдЪрди', 'рд╕рд┐рдВрдЪрд╛рдИ', 'р▓ир│Ар▓░р│Б', 'родрогрпНрогрпАро░рпН', 'р░ир▒Ар░░р▒Б', 'р┤╡р╡Жр┤│р╡Нр┤│р┤В', 'рккрк╛ркгрлА', 'рикри╛ригрйА', 'рдЙрджрдХ']):
        return FARMING_KNOWLEDGE['irrigation'].get(language_code, FARMING_KNOWLEDGE['irrigation']['en'])
    
